mapper.select:
SELECT DISTINCT stu_id,stu_name FROM( SELECT lsn.stu_id, lsn.stu_name, sche.new_scanqr_date FROM t_info_lesson_extra_to_sche sche INNER JOIN v_info_lesson lsn ON sche.lesson_id = lsn.lesson_id ) pullDownName WHERE 1=1
AND left(new_scanqr_date,4) = #{year}
AND EXISTS (SELECT 1 FROM t_mst_student WHERE del_flg = 0) ORDER BY stu_name

SELECT * FROM( SELECT lsn.stu_id, lsn.stu_name, sche.lesson_id, sub.subject_name, oldEda.subject_sub_name AS old_subject_sub_name, old_lsn_fee, newEda.subject_sub_name AS new_subject_sub_name, new_lsn_fee, lsn.scanqr_date AS from_extra_scan_date, sche.new_scanqr_date AS to_sche_scan_date, sche.is_good_change, sche.memo_reason FROM t_info_lesson_extra_to_sche sche INNER JOIN t_mst_subject sub ON sche.subject_id = sub.subject_id INNER JOIN t_info_subject_edaban oldEda ON oldEda.subject_id = sche.subject_id AND oldEda.subject_sub_id = sche.old_subject_sub_id INNER JOIN t_info_subject_edaban newEda ON newEda.subject_id = sche.subject_id AND newEda.subject_sub_id = sche.new_subject_sub_id INNER JOIN v_info_lesson lsn ON sche.lesson_id = lsn.lesson_id ) pullDownName WHERE 1=1
AND stu_id = #{stuId}
AND left(to_sche_scan_date,4) = #{year}
AND is_good_change = #{isGoodChange}
AND EXISTS (SELECT 1 FROM t_mst_student WHERE del_flg = 0) ORDER BY stu_name
