mapper.select:
SELECT DISTINCT stu_id, stu_name FROM v_info_lesson lsn WHERE 1=1 AND lsn.lesson_type = 2
AND LEFT(lsn.schedual_date,4) = #{yearMonth}
AND LEFT(lsn.schedual_date,7) = #{yearMonth}
AND EXISTS (SELECT 1 FROM t_mst_student stu WHERE stu.stu_id = lsn.stu_id AND del_flg = 0) ORDER BY stu_name

WITH latest_subject_info_by_yyyymmdd AS ( SELECT subquery.stu_id AS stu_id, CASE WHEN subquery.del_flg = 1 THEN CONCAT(subquery.stu_name, '(已退学)') ELSE subquery.stu_name END AS stu_name, CASE WHEN subquery.del_flg = 1 THEN CONCAT(subquery.nik_name, '(已退学)') ELSE subquery.nik_name END AS nik_name, subquery.subject_id AS subject_id, subquery.subject_name AS subject_name, subquery.subject_sub_id AS subject_sub_id, subquery.subject_sub_name AS subject_sub_name, subquery.minutes_per_lsn AS minutes_per_lsn FROM ( SELECT vDoc.*, ROW_NUMBER() OVER ( PARTITION BY vDoc.stu_id, vDoc.subject_id, vDoc.subject_sub_id ORDER BY vDoc.adjusted_date DESC ) AS rn FROM v_info_student_document vDoc WHERE vDoc.adjusted_date <= CURDATE() ) subquery WHERE subquery.rn = 1 ) SELECT DISTINCT stu.stu_id, stu.stu_name, stu.nik_name FROM v_info_all_extra_lsns allextra INNER JOIN t_mst_student stu ON allextra.stu_id = stu.stu_id INNER JOIN t_mst_subject sub ON allextra.subject_id = sub.subject_id INNER JOIN t_info_subject_edaban eda ON allextra.subject_id = eda.subject_id AND allextra.subject_sub_id = eda.subject_sub_id INNER JOIN latest_subject_info_by_yyyymmdd doc ON allextra.stu_id = doc.stu_id AND allextra.subject_id = doc.subject_id AND allextra.subject_sub_id = doc.subject_sub_id AND allextra.class_duration = doc.minutes_per_lsn LEFT JOIN t_info_lesson_extra_to_sche tosche ON allextra.lesson_id = tosche.lesson_id
AND substring(schedual_date, 1, 4) = #{year}
ORDER BY stu_name

WITH latest_subject_info_by_yyyymmdd AS ( SELECT subquery.stu_id AS stu_id, CASE WHEN subquery.del_flg = 1 THEN CONCAT(subquery.stu_name, '(已退学)') ELSE subquery.stu_name END AS stu_name, CASE WHEN subquery.del_flg = 1 THEN CONCAT(subquery.nik_name, '(已退学)') ELSE subquery.nik_name END AS nik_name, subquery.subject_id AS subject_id, subquery.subject_name AS subject_name, subquery.subject_sub_id AS subject_sub_id, subquery.subject_sub_name AS subject_sub_name, subquery.lesson_fee AS lesson_fee, subquery.lesson_fee_adjusted AS lesson_fee_adjusted, subquery.year_lsn_cnt AS year_lsn_cnt, subquery.minutes_per_lsn AS minutes_per_lsn, subquery.pay_style AS pay_style, subquery.adjusted_date AS adjusted_date, subquery.del_flg AS del_flg FROM ( SELECT vDoc.*, ROW_NUMBER() OVER ( PARTITION BY vDoc.stu_id, vDoc.subject_id, vDoc.subject_sub_id ORDER BY vDoc.adjusted_date DESC ) AS rn FROM v_info_student_document vDoc WHERE vDoc.adjusted_date <=
#{adjustedBaseDate}
CURDATE()
) subquery WHERE subquery.rn = 1 ) SELECT stu.stu_name, sub.subject_name, eda.subject_sub_name, allextra.lesson_id, allextra.stu_id, allextra.subject_id, allextra.subject_sub_id, allextra.class_duration, allextra.lesson_type, allextra.schedual_type, allextra.schedual_date, allextra.lsn_adjusted_date, allextra.extra_to_dur_date, allextra.scanqr_date, case when allextra.extra_to_dur_date is not null then allextra.schedual_date else null end as original_schedual_date, case when tosche.new_own_flg = 1 then 1 else allextra.pay_flg end as pay_flg, tosche.is_good_change, tosche.memo_reason FROM v_info_all_extra_lsns allextra INNER JOIN t_mst_student stu ON allextra.stu_id = stu.stu_id INNER JOIN t_mst_subject sub ON allextra.subject_id = sub.subject_id INNER JOIN t_info_subject_edaban eda ON allextra.subject_id = eda.subject_id AND allextra.subject_sub_id = eda.subject_sub_id INNER JOIN latest_subject_info_by_yyyymmdd doc ON allextra.stu_id = doc.stu_id AND allextra.subject_id = doc.subject_id AND allextra.subject_sub_id = doc.subject_sub_id AND allextra.class_duration = doc.minutes_per_lsn LEFT JOIN t_info_lesson_extra_to_sche tosche ON allextra.lesson_id = tosche.lesson_id
AND allextra.stu_id = #{stuId}
AND substring(allextra.schedual_date,1,4) = #{year}

WITH latest_subject_info_by_yyyymmdd AS ( SELECT subquery.stu_id AS stu_id, CASE WHEN subquery.del_flg = 1 THEN CONCAT(subquery.stu_name, '(已退学)') ELSE subquery.stu_name END AS stu_name, CASE WHEN subquery.del_flg = 1 THEN CONCAT(subquery.nik_name, '(已退学)') ELSE subquery.nik_name END AS nik_name, subquery.subject_id AS subject_id, subquery.subject_name AS subject_name, subquery.subject_sub_id AS subject_sub_id, subquery.subject_sub_name AS subject_sub_name, subquery.lesson_fee AS lesson_fee, subquery.lesson_fee_adjusted AS lesson_fee_adjusted, subquery.year_lsn_cnt AS year_lsn_cnt, subquery.minutes_per_lsn AS minutes_per_lsn, subquery.pay_style AS pay_style, subquery.adjusted_date AS adjusted_date, subquery.del_flg AS del_flg FROM ( SELECT vDoc.*, ROW_NUMBER() OVER ( PARTITION BY vDoc.stu_id, vDoc.subject_id, vDoc.subject_sub_id ORDER BY vDoc.adjusted_date DESC ) AS rn FROM v_info_student_document vDoc WHERE vDoc.adjusted_date <=
#{params.adjustedBaseDate}
CURDATE()
) subquery WHERE subquery.rn = 1 ),
extra_lessons AS ( SELECT stu.stu_name, sub.subject_name, eda.subject_sub_name, allextra.lesson_id, allextra.stu_id, allextra.subject_id, allextra.subject_sub_id, allextra.class_duration, allextra.lesson_type, allextra.schedual_type, allextra.schedual_date, allextra.lsn_adjusted_date, allextra.extra_to_dur_date, allextra.scanqr_date, CASE WHEN allextra.extra_to_dur_date IS NOT NULL THEN allextra.schedual_date ELSE NULL END AS original_schedual_date, CASE WHEN tosche.new_own_flg = 1 THEN 1 ELSE allextra.pay_flg END AS pay_flg, tosche.is_good_change, tosche.memo_reason FROM v_info_all_extra_lsns allextra INNER JOIN t_mst_student stu ON allextra.stu_id = stu.stu_id INNER JOIN t_mst_subject sub ON allextra.subject_id = sub.subject_id INNER JOIN t_info_subject_edaban eda ON allextra.subject_id = eda.subject_id AND allextra.subject_sub_id = eda.subject_sub_id INNER JOIN latest_subject_info_by_yyyymmdd doc ON allextra.stu_id = doc.stu_id AND allextra.subject_id = doc.subject_id AND allextra.subject_sub_id = doc.subject_sub_id AND allextra.class_duration = doc.minutes_per_lsn LEFT JOIN t_info_lesson_extra_to_sche tosche ON allextra.lesson_id = tosche.lesson_id
AND substring(allextra.schedual_date,1,4) = #{params.lsn_month}
AND substring(allextra.schedual_date,1,7) = #{params.lsn_month}
AND allextra.stu_id = #{params.stu_id}
) SELECT * FROM extra_lessons
AND pay_flg = #{params.extra_lsn_pay_status}

SELECT stu.stu_name, sub.subject_name, eda.subject_sub_name, allextra.lesson_id, allextra.stu_id, allextra.subject_id, allextra.subject_sub_id, allextra.class_duration, allextra.lesson_type, allextra.schedual_type, allextra.schedual_date, allextra.lsn_adjusted_date, allextra.extra_to_dur_date, allextra.scanqr_date, case when allextra.extra_to_dur_date is not null then allextra.schedual_date else null end as original_schedual_date, case when tosche.new_own_flg = 1 then 1 else allextra.pay_flg end as pay_flg, tosche.is_good_change, tosche.memo_reason FROM v_info_all_extra_lsns allextra INNER JOIN t_mst_student stu ON allextra.stu_id = stu.stu_id INNER JOIN t_mst_subject sub ON allextra.subject_id = sub.subject_id INNER JOIN t_info_subject_edaban eda ON allextra.subject_id = eda.subject_id AND allextra.subject_sub_id = eda.subject_sub_id
LEFT JOIN t_info_lesson_extra_to_sche tosche ON allextra.lesson_id = tosche.lesson_id
allextra.lesson_id = #{lessonId}


WITH DateRanges AS (
    SELECT adjusted_date
    FROM v_info_student_document 
    WHERE stu_id = #{stuId} 
    AND subject_id = #{subjectId}
    ORDER BY adjusted_date ASC
)
SELECT * 
FROM v_info_student_document doc
WHERE doc.stu_id = #{stuId}
AND doc.subject_id = #{subjectId}
AND CASE 
    WHEN #{yearMonth} < (SELECT MIN(LEFT(adjusted_date,7)) FROM DateRanges) THEN 1
    WHEN #{yearMonth} >= (
        SELECT MAX(LEFT(adjusted_date,7)) FROM DateRanges
    ) THEN (SELECT COUNT(adjusted_date) + 1 FROM DateRanges)
    ELSE (
        SELECT COUNT(*) + 1 
        FROM DateRanges d 
        WHERE LEFT(d.adjusted_date,7) <= #{yearMonth}
    )
END = CASE 
    WHEN LEFT(doc.adjusted_date,7) < (SELECT MIN(LEFT(adjusted_date,7)) FROM DateRanges) THEN 1
    WHEN LEFT(doc.adjusted_date,7) >= (
        SELECT MAX(LEFT(adjusted_date,7)) FROM DateRanges
    ) THEN (SELECT COUNT(adjusted_date) + 1 FROM DateRanges)
    ELSE (
        SELECT COUNT(*) + 1 
        FROM DateRanges d 
        WHERE LEFT(d.adjusted_date,7) <= LEFT(doc.adjusted_date,7)
    )
END

mapper.update:

update t_info_lesson_fee

    lsn_fee = #{lsnFee},
    del_flg = #{delFlg}

where lesson_id = #{lessonId}

mapper.select:
select lsn_fee_id
      ,own_flg
from v_info_lesson_fee_connect_lsn_and_extraToScheDataCorrect

    stu_id = #{stuId}
    and subject_id = #{subjectId}
    and lsn_month = #{lsnMonth}
    and lesson_type = #{lessonType}

select old_lsn_fee as lsn_fee
from t_info_lesson_extra_to_sche

    lesson_id = #{lessonId}

mapper.update:
