mapper.select:
select * from t_info_lesson_fee
where lsn_fee_id = #{lsnFeeId}
  and lesson_id = #{lessonId}

mapper.update:
update t_info_lesson_fee set
    lsn_fee_id = #{lsnFeeId},
    lesson_id = #{lessonId},
    lsn_fee = #{lsnFee},
    lsn_month = #{lsnMonth},
    own_flg = #{ownFlg},
    del_flg = #{delFlg}
where lsn_fee_id = #{lsnFeeId}
    and lesson_id = #{lessonId}
