mapper.select:

select * from v_info_lesson_fee_connect_lsn 


        substring(lsn_month, 1, 4) = #{year}


order by stu_name,lsn_month

select * from v_info_lesson_fee_connect_lsn
where lsn_fee_id = #{lsnFeeId}
  and lesson_id = #{lessonId}

select * from v_info_lesson_fee_connect_lsn
where stu_id = #{stuId}
  and subject_id = #{subjectId}
  and lesson_type = #{lessonType}
  and lsn_month = #{lsnMonth}

mapper.update:
update t_info_lesson_fee
lsn_fee_id = #{lsnFeeId}, lesson_id = #{lessonId}, pay_style = #{payStyle}, lsn_fee = #{lsnFee}, lsn_month = #{lsnMonth}, own_flg = #{ownFlg}, del_flg = #{delFlg},
where lsn_fee_id = #{lsnFeeId} and lesson_id = #{lessonId}

update t_info_lesson_fee set
    own_flg = #{ownFlg}
where lsn_fee_id = #{lsnFeeId}
