mapper.select:

select distinct stu_id,stu_name,nik_name from v_info_lesson_fee_connect_lsn_and_extraToScheDataCorrect 


        substring(lsn_month, 1, 4) = #{year}




order by stu_name

select * from v_info_lesson_fee_connect_lsn_and_extraToScheDataCorrect
where lsn_fee_id = #{lsnFeeId}
  and lesson_id = #{lessonId}

select * from v_info_lesson_fee_connect_lsn_and_extraToScheDataCorrect

    stu_id = #{stuId}
    and subject_id = #{subjectId}
    and lsn_month = #{lsnMonth}
    and lesson_type = #{lessonType}

SELECT 
    main.*,
    CASE 
        WHEN sub.lesson_id IS NOT NULL THEN 1 
        ELSE 0 
    END AS is_from_picese_lsn
FROM v_info_lesson_fee_connect_lsn_and_extraToScheDataCorrect main
LEFT JOIN (

    SELECT DISTINCT lesson_id 
    FROM t_info_lesson_pieces_extra_to_sche
) sub 
ON main.lesson_id = sub.lesson_id





                AND substring(main.lsn_month,1,4) = #{params.lsn_month}


                AND main.lsn_month = #{params.lsn_month}




        AND main.stu_id = #{params.stu_id}


        AND main.lsn_fee_id = #{params.lsn_fee_id}


        AND main.lesson_id = #{params.lesson_id}


        AND main.lesson_type = #{params.lesson_type}


        AND main.own_flg = #{params.own_flg}


ORDER BY main.stu_name, main.lsn_month desc, main.lesson_type, main.subject_id, main.lesson_id desc

select stu_id ,stu_name ,lsn_pay_id ,lsn_fee_id ,subject_id ,subject_name ,subject_sub_name ,subject_price ,pay_style ,lesson_type ,lsn_count ,lsn_fee ,0 as lsn_pay ,lsn_month ,pay_date ,own_flg ,1 as advc_flg ,'' as bank_name from v_info_lesson_sum_fee_unpaid_yet
stu_id = #{stuId}
AND left(lsn_month, 4) = #{yearMonth}
AND left(lsn_month, 7) = #{yearMonth}
union all select paid.stu_id ,paid.stu_name ,paid.lsn_pay_id ,paid.lsn_fee_id ,paid.subject_id ,paid.subject_name ,paid.subject_sub_name ,paid.subject_price ,paid.pay_style ,paid.lesson_type ,paid.lsn_count ,paid.lsn_fee ,paid.lsn_pay ,paid.lsn_month ,paid.pay_date ,1 as own_flg ,lfap.advc_flg ,bnk.bank_name from v_info_lesson_sum_fee_pay_over paid left join t_info_lsn_fee_advc_pay lfap on paid.lsn_pay_id = lfap.lsn_pay_id left join t_mst_bank bnk on paid.bank_id = bnk.bank_id
stu_id = #{stuId}
AND left(lsn_month, 4) = #{yearMonth}
AND left(lsn_month, 7) = #{yearMonth}
order by lsn_month, subject_id;

mapper.update:
update t_info_lesson_fee
lsn_fee_id = #{lsnFeeId}, lesson_id = #{lessonId}, pay_style = #{payStyle}, lsn_fee = #{lsnFee}, lsn_month = #{lsnMonth}, own_flg = #{ownFlg}, del_flg = #{delFlg},
where lsn_fee_id = #{lsnFeeId} and lesson_id = #{lessonId}

update t_info_lesson_fee set
    own_flg = #{ownFlg}
where lsn_fee_id = #{lsnFeeId}

update t_info_lesson_extra_to_sche

    new_own_flg = #{newOwnFlg}

where new_lsn_fee_id = #{lsnFeeId}
and substring(new_scanqr_date,1,7) = #{payMonth}
