mapper.select:
SELECT lsn.stu_id, lsn.stu_name FROM v_info_lesson lsn INNER JOIN t_info_lsn_fee_advc_pay advc ON lsn.lesson_id = advc.lesson_id INNER JOIN t_info_lesson_pay pay ON pay.lsn_pay_id = advc.lsn_pay_id AND advc.lsn_fee_id = pay.lsn_fee_id INNER JOIN t_mst_bank bnk ON pay.bank_id = bnk.bank_id
substring(schedual_date,1,4) = #{year}
ORDER BY lsn.stu_name

SELECT advc.lesson_id, advc.lsn_fee_id, advc.lsn_pay_id, lsn.stu_id, lsn.stu_name, lsn.nik_name, lsn.subject_id, lsn.subject_name, lsn.subject_sub_id, lsn.subject_sub_name, lsn.schedual_date, pay.lsn_pay, pay.bank_id, bnk.bank_Name, advc.advc_flg, CASE WHEN advc.advc_flg = 1 THEN 0 WHEN advc.advc_flg = 0 AND EXISTS ( SELECT 1 FROM t_info_lesson t_check WHERE t_check.stu_id = lsn.stu_id AND t_check.subject_id = lsn.subject_id AND LEFT(t_check.schedual_date, 7) = LEFT(lsn.schedual_date, 7) AND t_check.scanqr_date IS NOT NULL ) THEN 1 WHEN advc.advc_flg = 0 THEN 0 ELSE -1 END AS data_status FROM v_info_lesson lsn INNER JOIN t_info_lsn_fee_advc_pay advc ON lsn.lesson_id = advc.lesson_id INNER JOIN t_info_lesson_pay pay ON pay.lsn_pay_id = advc.lsn_pay_id AND advc.lsn_fee_id = pay.lsn_fee_id INNER JOIN t_mst_bank bnk ON pay.bank_id = bnk.bank_id
AND stu_id = #{stuId}
AND substring(schedual_date,1,4) = #{year}
ORDER BY schedual_date DESC

SELECT lesson_id 
FROM v_info_lesson 


        AND stu_id = #{stuId}


        AND subject_id = #{subjectId}

    AND LEFT(schedual_date, 7) = (
        SELECT LEFT(schedual_date, 7) 
        FROM t_info_lesson 
        WHERE lesson_id = #{lessonId}
    )
    AND scanqr_date IS NOT NULL

ORDER BY schedual_date ASC
LIMIT 1;

mapper.update:
UPDATE t_info_lsn_fee_advc_pay
SET
    lesson_id = #{lessonId},
    advc_flg = 1
WHERE lesson_id = #{oldLessonId}
AND   lsn_fee_id = #{lsnFeeId}
AND   lsn_pay_id = #{lsnPayId}
