mapper.select:
SELECT stu.stu_id, stu.stu_name, stu.nik_name, MIN(scanqr_date) AS enter_date, MAX(scanqr_date) AS quit_date FROM v_info_lesson lsn INNER JOIN t_mst_student stu ON lsn.stu_id = stu.stu_id
AND stu.stu_id = #{stuId}
AND stu.del_flg = #{delFlg}
GROUP BY stu.stu_id,stu.stu_name,stu.nik_name UNION ALL
SELECT mst.stu_id, mst.stu_name, mst.nik_name, NULL AS start_date, NULL AS end_date FROM t_mst_student mst
AND mst.stu_id = #{stuId}
AND mst.del_flg = #{delFlg}
AND NOT EXISTS ( SELECT 1 FROM v_info_lesson lsn WHERE lsn.stu_id = mst.stu_id )
ORDER BY stu_name

mapper.update:
update t_mst_student set
del_flg = 1
where stu_id = #{stuId}

update t_mst_student set del_flg = 1
where stu_id in

    #{bean.stuId}

update t_mst_student set
del_flg = 0
where stu_id = #{stuId}
