mapper.select:
select * from t_info_student_bank
 where stu_id = #{stuId}
   and bank_id = #{bankId}

mapper.update:
update t_info_student_bank set
        stu_id = #{stuId},
        bank_id = #{bankId},

        del_flg = 0
where stu_id = #{stuId}
  and bank_id = #{bankId}

mapper.insert:
insert into t_info_student_bank (
    stu_id,
    bank_id
    ) values (
    #{stuId},
    #{bankId}
)

mapper.delete:
delete from t_info_student_bank
 where stu_id = #{stuId}
   and bank_id = #{bankId}
