mapper.select:
select * from t_info_lesson_pay
where lsn_pay_id = #{lsnPayId}
  and lsn_fee_id = #{lsnFeeId}

mapper.update:
update t_info_lesson_pay set
    lsn_pay_id = #{lsnPayId},
    lsn_fee_id = #{lsnFeeId},
    lsn_pay = #{lsnPay},
    pay_month = #{payMonth},
    bank_id = #{bankId},
    del_flg = #{delFlg}
where lsn_pay_id = #{lsnPayId}
  and lsn_fee_id = #{lsnFeeId}
